mapper.update:
UPDATE Account 
SET password = #{param2}
WHERE ID = #{param1}

UPDATE Account 
SET Email=#{email}, PostCode=#{postCode}, RoadAddress=#{roadAddress},
	DetailAddress=#{detailAddress}, Phone=#{phone}, Name=#{name}, 
	BirthYear=#{birthYear}, BirthMonth=#{birthMonth}, BirthDay=#{birthDay}, Gender=#{gender}
WHERE ID = #{id}

mapper.insert:
INSERT INTO
Account (ID, Password, Email, PostCode, RoadAddress, DetailAddress, Phone, Name, BirthYear, BirthMonth, BirthDay, Gender)
VALUES (#{id}, #{password}, #{email}, #{postCode}, #{roadAddress}, #{detailAddress}, #{phone}, #{name}, #{birthYear}, #{birthMonth}, #{birthDay}, #{gender});

mapper.select:
SELECT Name, Email, BirthYear, BirthMonth, BirthDay, Gender, Phone,
PostCode, RoadAddress, DetailAddress

FROM Account WHERE ID = #{id};
