mapper.select:
SELECT ifnull(SUM(p.price * c.orderCount), 0) money
FROM cartlist c, productlist p
WHERE c.no = p.no AND c.id = #{id}

mapper.update:
UPDATE cartlist 
SET orderCount = #{orderCount} 
WHERE id= #{id} 
AND no = #{no}

mapper.delete:
DELETE FROM cartlist 
WHERE id = #{param1}
and no = #{param2}

mapper.select:
SELECT COUNT(*)
FROM cartlist
WHERE id= #{param2} 
AND no = #{param1}

mapper.update:
UPDATE cartlist 
SET orderCount = orderCount + #{orderCount}    
WHERE id= #{id} 
AND no = #{no}
